from collections import Counter
class Solution:
    def longestPalindrome(self, s: str) -> int:
        sc = Counter(s)
        length = 0
        single = 0
        for key in sc.keys():
            if sc[key] % 2 == 0:
                length += sc[key]
            else:
                length += sc[key] - 1
                single = 1
        if single == 0:
            return length
        else:
            return length + 1

solution = Solution()
print(solution.longestPalindrome(""))
print(solution.longestPalindrome(s = "abccccdd"))
print(solution.longestPalindrome(s = "a"))